        self.assertEqual(hex_encode(cert.serial_number()), "41d29dd172eaeea780c12c6ce92f8752")
        self.assertEqual(hex_encode(cert.subject_key_id()),
                         "7c4296aede4b483bfa92f89e8ccf6d8ba9723795")

