        self.assertTrue(cert.allowed_usage(["KEY_CERT_SIGN"]))
        self.assertTrue(cert.allowed_usage([botan.X509KeyConstraints.CRL_SIGN, botan.X509KeyConstraints.KEY_CERT_SIGN]))
